
public class problem8 {
	public static void main(String args[]){
		String bigNum   = "";
		int max = 0;
		long time = System.currentTimeMillis();
		for(int i=0;i<1000;i++){
			String a = bigNum.substring(i,i+1);
			String b = bigNum.substring(i+1,i+2);
			String c = bigNum.substring(i+2,i+3);
			String d = bigNum.substring(i+3,i+4);
			String e = bigNum.substring(i+4,i+5);
			int    v = Integer.parseInt(a);
			int    w = Integer.parseInt(b);
			int    x = Integer.parseInt(c);
			int    y = Integer.parseInt(d);
			int    z = Integer.parseInt(e);
			if(v*w*x*y*z > max){
				max = v*w*x*y*z;
			}
		}
		System.out.println(max);
		System.out.println("Completed in " + (System.currentTimeMillis() - time) + "ms");
	}
}
